var stateChange = require('../lib/stateChange');
var should = require('should');
var noble = require('noble');


describe('stateChange', function(){
  describe('when a user disables bluetooth', function(){
    it('should stop scanning for bluetooth devices', function(done){
      this.timeout(false);
    
      noble.on('stateChange', stateChange.bind({noble: noble}))

      noble.on('scanStart', function(){
        true.should.equal(true);       
        done();
      });

    });
  });
});